import React from "react";

function CreateArea() {
    const [change, newChange] = React.useState("");

    function changed(event){
        newChange()
    }

    return (
        <div>
            <form>
                <input onChange={changed} name="title" placeholder="Title" />
                <textarea name="content" placeholder="Take a note..." rows="3" />
                <button>Add</button>
            </form>
        </div>
    );
}

export default CreateArea;
